# Definición del nombre de función y argumentos de entrada
calcular_area <- function(lado_1, lado_2 = lado_1) {

  # Cuerpo de la función
  area <- lado_1 * lado_2

  # Resultado que devolvemos
  return(area)

}
calcular_area(lado_1 = 5) # si no indicamos nada, lado_2 = lado_1## [1] 25
# Definición del nombre de función y argumentos de entrada
calcular_area <- function(lado_1, lado_2 = lado_1) {

  # Cuerpo de la función
  area <- lado_1 * lado_2

  # Resultado
  return(c("area" = area, "lado_1" = lado_1, "lado_2" = lado_2))

}
salida <- calcular_area(5, 3)
salida##   area lado_1 lado_2 
##     15      5      3
salida["area"]## area 
##   15
salida["lado_1"]## lado_1 
##      5
salida["lado_2"]## lado_2 
##      3
